// ----------------------------------------------------------------------------
// File Path: src/root-styles/_mixins.scss
// Description: SCSS mixins used throughout all scss files
// Author: Dan Levy
// Email: danlevy124@gmail.com
// Created Date: 10/23/2019
// ----------------------------------------------------------------------------

// Formats text
@mixin text($size, $weight, $color, $line-height) {
    font-size: $size;
    font-weight: $weight;
    color: $color;
    line-height: $line-height;
}

// Media query
@mixin media-query($breakpoint) {
    @media screen and (min-width: $breakpoint) {
        @content;
    }
}

// Flexbox settings
@mixin flex-container($direction, $justify-content, $align-items, $wrap) {
    display: flex;
    flex-direction: $direction;
    justify-content: $justify-content;
    align-items: $align-items;
    flex-wrap: $wrap;
}

@mixin grid-container($grid-template-rows, $grid-template-columns, $align-items) {
    display: grid;
    grid-template-rows: $grid-template-rows;
    grid-template-columns: $grid-template-columns;
    align-items: $align-items;
}

